<?php

return [

	/**
	 * Name of the application/service.
	 */
	"name" => "LibraryService",

	/**
	 * Current running environment.
	 */
	"env" => \getenv("ENV"),

	/**
	 * Base timezone.
	 *
	 * All dates and timestamps generated by the application will be in this timezone
