	 * Component specific providers (http, queue consumer, and scheduler) are registered
	 * separately.
	 */
	"providers" => [
		App\Providers\LoggerProvider::class,
		App\Providers\DatabaseProvider::class,
		App\Providers\MigrationsProvider::class,
		App\Providers\CommandProvider::class,
		App\Providers\HttpClientProvider::class
	],

	/**
	 * Registered CLI commands.
	 */
	"commands" => [
		"shell" => App\Commands\Shell::class,
		"migrate" => App\Commands\Migrate::class
	]
